import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import "./App.css";
import { Route, Switch } from "react-router";
import { render } from "@testing-library/react";

function App() {
  render();
  return (
    <Switch>
      <Route exact path="/">
        <Home />
      </Route>
      <Route path="/about">
        <About />
      </Route>
      <Route path="/:user">
        <User />
      </Route>
      <Route>
        <NoMatch />
      </Route>
    </Switch>
  );
}

